const API_URL="http://localhost:8080";function enviarDados(e,t,n){return fetch(e,{method:n,headers:{"Content-Type":"application/json"},body:JSON.stringify(t)}).then(e=>e.json()).catch(e=>console.error("Erro na requisição:",e))}function inserirCurso(){var e=document.getElementById("titleInput").value,t=document.getElementById("textInput").value,n=document.getElementById("linkInput").value,o=document.getElementById("imageInput").value;fetch(API_URL+"/src/controllers/cursoController.php",{method:"POST",headers:{"Content-Type":"application/x-www-form-urlencoded"},body:`action=insert&TITLE=${e}&TEXT=${t}&LINK=${n}&IMAGE=`+o}).then(e=>{document.getElementById("modal-crud").style.display="none",e.json(),location.reload()}).catch(e=>{console.error("Erro:",e),alert(e)})}function atualizarCurso(){var e=document.getElementById("idInput").value,t=document.getElementById("titleInput").value,n=document.getElementById("textInput").value,o=document.getElementById("linkInput").value,r=document.getElementById("imageInput").value;fetch(API_URL+"/src/controllers/cursoController.php",{method:"POST",headers:{"Content-Type":"application/x-www-form-urlencoded"},body:`action=update&ID=${e}&TITLE=${t}&TEXT=${n}&LINK=${o}&IMAGE=`+r}).then(e=>{document.getElementById("modal-crud").style.display="none",e.json()}).catch(e=>{console.error("Erro:",e),alert(e)})}function excluirCurso(){console.log("aaaa");var e=document.getElementById("idInput").value;fetch(API_URL+"/src/controllers/cursoController.php",{method:"POST",headers:{"Content-Type":"application/x-www-form-urlencoded"},body:"action=delete&ID="+e}).then(e=>{document.getElementById("modal-crud").style.display="none",e.json(),location.reload()}).catch(e=>{console.error("Erro:",e),alert(e)})}